mapper.select:
SELECT t1.id,t1.name,t1.tch_id,t1.nick_name,t1.time,t1.room FROM mxg_course t1 LEFT JOIN mxg_class t3 ON t1.class_id = t3.id LEFT JOIN mxg_student t4 ON t4.class = t3.id WHERE t4.name=#{req.username}
AND t1.name LIKE CONCAT('%', #{req.course}, '%')
AND t1.nick_name LIKE CONCAT('%', #{req.teacher}, '%')
UNION ALL SELECT t6.* FROM mxg_student_elective_course t5 LEFT JOIN mxg_elective_course t6 ON t5.id = t6.id WHERE t5.name=#{req.username}
AND t6.name LIKE CONCAT('%', #{req.course}, '%')
AND t6.nick_name LIKE CONCAT('%', #{req.teacher}, '%')
